import React, { Component } from 'react';

import DashBoardNavBar from '../DashBoardNav/DashBoardNavBar'
import profile from '../../assets/img/undraw_profile_pic_ic5t.svg'
import  './AbsenceForm.css'


class AbsenceForm extends Component {
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.validator = new FormValidator([
            { 
                field: 'company', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'Company Name is required' 
              },
              { 
                field: 'firstname', 
                method: 'isEmpty',
                validWhen: false, 
                message: 'First Name is required' 
							},
							{ 
                field: 'firstname', 
								method: 'matches',
								args: [/^[a-zA-Z]*$/],
                validWhen: true, 
                message: 'First Name can only contain letters' 
              },
              { 
                field: 'lastname', 
                method: 'isEmpty',
                validWhen: false, 
                message: 'Last Name is required' 
							},
							{ 
                field: 'lastname', 
                method: 'matches',
								args: [/^[a-zA-Z]*$/],
                validWhen: true, 
                message: 'Last Name can only contain letters' 
              },
            { 
              field: 'email', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Email is required' 
            },
            { 
              field: 'email',
              method: 'isEmail', 
              validWhen: true, 
              message: 'That is not a valid email.'
            },
            { 
              field: 'password', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Password is required'
            },
            { 
              field: 'confirmpassword', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Password confirmation is required'
            },
            { 
              field: 'confirmpassword', 
              method: this.passwordMatch,   // notice that we are passing a custom function here
              validWhen: true, 
              message: 'Password and password confirmation do not match.'
            },
            { 
                field: 'age', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'Age is required'
						},
						{
							field: 'age',
    					method: 'isInt',
    					args: [{min: 16, max: 70}],  // an array of additional arguments
    					validWhen: true,
    					message: 'Your age must be an integer between 16 and 70'
						},
						{ 
							field: 'department', 
							method: 'isEmpty', 
							validWhen: false, 
							message: 'Work Department is required'
						},
						{ 
							field: 'department', 
							method: 'matches',
							args: [/^[a-zA-Z]*$/],
							validWhen: true, 
							message: 'Department details can only contain letters' 
						},
						{ 
							field: 'manager', 
							method: 'isEmpty', 
							validWhen: false, 
							message: 'Manager details is required'
						},
						{ 
							field: 'manager', 
							method: 'matches',
							args: [/^[a-zA-Z]*$/],
							validWhen: true, 
							message: 'Manager details can only contain letters' 
						},
						{ 
							field: 'dob', 
							method: 'isEmpty', 
							validWhen: false, 
							message: 'Date of Birth is required'
						},
						{ 
							field: 'country', 
							method: 'isEmpty', 
							validWhen: false, 
							message: 'Country is required'
						},
						{ 
							field: 'timezone', 
							method: 'isEmpty', 
							validWhen: false, 
							message: 'Time Zone is required'
						} 
          ]);

          this.state = {
            data: {
              firstname: 'Ayooluwa',
              profilepic: profile,
              timeofftypes: [
                'Holiday', 
                'Maternity Leave', 
                'Paternity Leave', 
                'Sick Leave (Up to 10 Days)'
            ],
            },
            company:'',
            email: '',
            firstname: '',
            lastname: '',
            password: '',
            confirmpassword: '',
						age: '',
						dob: '',
						country:'',
						timezone:'',
            manager: '',
            department: '',
						validation: this.validator.valid(),
						successmessage: '',
						errormessage: '',
          }

          this.submitted = false;



		}

		passwordMatch = (confirmation, state) => (state.password === confirmation)

		handleInputChange = event => {
			event.preventDefault();

			this.setState({
				[event.target.name]: event.target.value,
			});
		}

    handleFormSubmit = event => {
        event.preventDefault();
				const refreshPage = (timeoutPeriod) => {
					let refresh = "location.reload(true)";
					setTimeout( refresh ,timeoutPeriod);
			  }
        const validation = this.validator.validate(this.state);
        this.setState({ validation });
        this.submitted = true;

        if (validation.isValid) {
					// handle actual form submission here
					this.setState({
						successmessage: 'New Absence Created successfully',errormessage: ''
					})
					refreshPage(5000);
					alert('Registered successfully')

        } else {
					this.setState({
						errormessage: 'Cannot Create New Response Make sure all fields are correctly filled', successmessage: ''
					})
					// alert('Cannot Create User Make sure all fields are correctly filled')
        }

  }
  render() {
    const timeOffTypes = this.state.data.timeofftypes;
        let offTypes = timeOffTypes.map( (type, index) => 
            <option value={type} key={index}> {type} </option>

        );
        let validation = this.submitted ?                         // if the form has been submitted at least once
        this.validator.validate(this.state) :   // then check validity every time we render
        this.state.validation     
    return (
      <div>
        <DashBoardNavBar data={this.state.data} />
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-2"></div>
            <div className="col-md-8">
              <div className="form">
              <h5 className="form-header text-center">Create New Absence</h5>

                <form>

                    <div className="form-group ">
                      <label for="inputEmail4"> Type Of Leave</label>
                      <select id="inputState" className="form-control">
                        <option selected>Choose Leave Type</option>
                        {offTypes}
                      </select>


                  </div>
                  <div className="form-row">
                  <div className="form-group col-md-4">
                    <label for="inputAddress">From</label>
                    <input type="date" className="form-control" id="inputAddress" placeholder="From" />
                  </div>
                  <div className="form-group col-md-4">
                    <label for="inputAddress2">To</label>
                    <input type="date" className="form-control" id="inputAddress2" placeholder="To" />
                  </div>
                  <div className="form-group col-md-4">
                    <label for="inputAddress2">Duration</label>
                    <input type="text" className="disabled form-control" id="inputAddress2" placeholder="Duration" />
                  </div>
                  </div>
                  <div className="form-group">
                    <label for="inputAddress">Reason For Leave</label>
                    <textarea rows="6" type="date" className="form-control" id="inputAddress" placeholder="Reason For Leave" />
                  </div>

                  <button type="submit" className="btn btn-primary">CREATE</button>
                  <button type="submit" className="btn btn-info">CANCEL</button>

                </form>







              </div>
            </div>
            <div className="col-md-2"></div>

          </div>
        </div>
      </div>
    )
  }
}

export default AbsenceForm;
