import React, { Component } from 'react';
import Footer from '../Footer/Footer'
import {Link} from 'react-router-dom'
import adduser from '../../assets/img/undraw_add_user_ipe3.svg'
import FormValidator from '../FormValidator/FormValidator'
import LogInNavBar from '../LogInNavBar/LogInNavBar'
import './LogInPage.css'

class LogInPage extends Component {
    constructor(props){
        super(props);
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    this.validator = new FormValidator([
    { 
        field: 'company', 
        method: 'isEmpty', 
        validWhen: false, 
        message: 'Company Name is required' 
      },
      { 
        field: 'firstname', 
        method: 'isEmpty',
        validWhen: false, 
        message: 'First Name is required' 
                    },
                    { 
        field: 'firstname', 
                        method: 'matches',
                        args: [/^[a-zA-Z]*$/],
        validWhen: true, 
        message: 'First Name can only contain letters' 
      },
      { 
        field: 'lastname', 
        method: 'isEmpty',
        validWhen: false, 
        message: 'Last Name is required' 
                    },
                    { 
        field: 'lastname', 
        method: 'matches',
                        args: [/^[a-zA-Z]*$/],
        validWhen: true, 
        message: 'Last Name can only contain letters' 
      },
    { 
      field: 'email', 
      method: 'isEmpty', 
      validWhen: false, 
      message: 'Email is required' 
    },
    { 
      field: 'email',
      method: 'isEmail', 
      validWhen: true, 
      message: 'That is not a valid email.'
    },
    { 
      field: 'phone', 
      method: 'isEmpty', 
      validWhen: false, 
      message: 'Pleave provide a phone number.'
    },
    {
      field: 'phone', 
      method: 'matches',
      args: [/^\(?\d\d\d\)? ?\d\d\d-?\d\d\d\d$/], // args is an optional array of arguements that will be passed to the validation method
      validWhen: true, 
      message: 'That is not a valid phone number.'
    },
    { 
      field: 'password', 
      method: 'isEmpty', 
      validWhen: false, 
      message: 'Password is required'
    },
    { 
      field: 'confirmpassword', 
      method: 'isEmpty', 
      validWhen: false, 
      message: 'Password confirmation is required'
    },
    { 
      field: 'confirmpassword', 
      method: this.passwordMatch,   // notice that we are passing a custom function here
      validWhen: true, 
      message: 'Password and password confirmation do not match.'
    },
    { 
        field: 'age', 
        method: 'isEmpty', 
        validWhen: false, 
        message: 'Age is required'
                },
                {
                    field: 'age',
                method: 'isInt',
                args: [{min: 16, max: 70}],  // an array of additional arguments
                validWhen: true,
                message: 'Your age must be an integer between 16 and 70'
                },
                { 
                    field: 'department', 
                    method: 'isEmpty', 
                    validWhen: false, 
                    message: 'Work Department is required'
                },
                { 
                    field: 'department', 
                    method: 'matches',
                    args: [/^[a-zA-Z]*$/],
                    validWhen: true, 
                    message: 'Department details can only contain letters' 
                },
                { 
                    field: 'manager', 
                    method: 'isEmpty', 
                    validWhen: false, 
                    message: 'Manager details is required'
                },
                { 
                    field: 'manager', 
                    method: 'matches',
                    args: [/^[a-zA-Z]*$/],
                    validWhen: true, 
                    message: 'Manager details can only contain letters' 
                },
                { 
                    field: 'dob', 
                    method: 'isEmpty', 
                    validWhen: false, 
                    message: 'Date of Birth is required'
                },
                { 
                    field: 'country', 
                    method: 'isEmpty', 
                    validWhen: false, 
                    message: 'Country is required'
                },
                { 
                    field: 'timezone', 
                    method: 'isEmpty', 
                    validWhen: false, 
                    message: 'Time Zone is required'
                } 
  ]);

  this.state = {
    company:'',
    email: '',
    firstname: '',
    lastname: '',
    phone: '',
    password: '',
    confirmpassword: '',
                age: '',
                dob: '',
                country:'',
                timezone:'',
    manager: '',
    department: '',
    validation: this.validator.valid(),
  }

  this.submitted = false;



}

passwordMatch = (confirmation, state) => (state.password === confirmation)

handleInputChange = event => {
    event.preventDefault();

    this.setState({
        [event.target.name]: event.target.value,
    });
}

handleFormSubmit = event => {
event.preventDefault();

const validation = this.validator.validate(this.state);
this.setState({ validation });
this.submitted = true;

if (validation.isValid) {
            // handle actual form submission here
            alert('Form submitted successfully')

}
}

    render(){
        let validation = this.submitted ?                         // if the form has been submitted at least once
        this.validator.validate(this.state) :   // then check validity every time we render
        this.state.validation 
        return(
            <div>
                <LogInNavBar/>
                <div className="container-fluid">
                    <div className="row">
                    <div className="col-sm-6 svg-img">
                        <img src={adduser} className="img-fluid" alt="timeoffImg"/>
                    </div>
                    <div className="col-sm-6 form">
                    <form action="" method="">
                        <div className="text-center">
                            <h5 className="form-header">Log In</h5>
                        </div>

                        <div className="form-group {validation.lastname.isInvalid && 'has-error'}">
                            <label htmlFor="email">Employee Email</label>
                            <input onChange={this.handleInputChange} type="email" name="email" className="form-control" id="email" placeholder="Enter email" />
                            <span className="help-block">{validation.email.message}</span>

                        </div>
                        <div className="form-group {validation.password.isInvalid && 'has-error'}">
                            <label htmlFor="password">Password</label>
                            <input type="password"  onChange={this.handleInputChange} name="password" className="form-control" id="password" placeholder="Password" />
                            <span className="help-block">{validation.password.message}</span>

                        </div>
                        <div className="text-center">

                            <button onClick={this.handleFormSubmit} className="btn btn-primary">LOGIN</button>
                        </div>
                        <div className="text-right">
                            <p className="registered"> <Link to="/login">Forgot Password?</Link></p>
                        </div>
                    </form>
                    </div>
                    </div>
                    </div>
                    </div>
        )
    }
}

export default LogInPage;
