import React, { Component } from 'react';
import FormValidator from '../FormValidator/FormValidator'

import DashBoardNavBar from '../DashBoardNav/DashBoardNavBar'
import profile from '../../assets/img/undraw_profile_pic_ic5t.svg'
import  './AbsenceForm.css'


class AbsenceForm extends Component {
  constructor(props) {
    super(props);
    this.handleInputChange = this.handleInputChange.bind(this);
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
        this.validator = new FormValidator([
            { 
                field: 'type', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'Type Of Leave is required' 
              },

              { 
                field: 'from', 
                method: 'isEmpty',
                validWhen: false, 
                message: 'Desired Start Date is required' 
              },
              { 
                field: 'to', 
                method: 'isEmpty',
                validWhen: false, 
                message: 'Desired End Date is required' 
              },
              { 
                field: 'duration', 
                method: 'isEmpty',
                validWhen: false, 
                message: 'Total Duration is required' 
							},
							{ 
                field: 'firstname', 
								method: 'matches',
								args: [/^[a-zA-Z]*$/],
                validWhen: true, 
                message: 'First Name can only contain letters' 
              },
            { 
              field: 'password', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Password is required'
            },
            { 
              field: 'confirmpassword', 
              method: 'isEmpty', 
              validWhen: false, 
              message: 'Password confirmation is required'
            },
            { 
              field: 'confirmpassword', 
              method: this.passwordMatch,   // notice that we are passing a custom function here
              validWhen: true, 
              message: 'Password and password confirmation do not match.'
            },
            { 
                field: 'age', 
                method: 'isEmpty', 
                validWhen: false, 
                message: 'Age is required'
						},
						{
							field: 'age',
    					method: 'isInt',
    					args: [{min: 16, max: 70}],  // an array of additional arguments
    					validWhen: true,
    					message: 'Your age must be an integer between 16 and 70'
						}

          ]);

          this.state = {
            data: {
              firstname: 'Ayooluwa',
              profilepic: profile,
              timeofftypes: [
                'Holiday', 
                'Maternity Leave', 
                'Paternity Leave', 
                'Sick Leave (Up to 10 Days)'
            ],
            },
            from:'',
            to: '',
            duration: '',
            reason: '',
						validation: this.validator.valid(),
						successmessage: '',
						errormessage: '',
          }

          this.submitted = false;



		}

		passwordMatch = (confirmation, state) => (state.password === confirmation)

		handleInputChange = event => {
			event.preventDefault();

			this.setState({
				[event.target.name]: event.target.value,
			});
		}

    handleFormSubmit = event => {
        event.preventDefault();
				const refreshPage = (timeoutPeriod) => {
					let refresh = "location.reload(true)";
					setTimeout( refresh ,timeoutPeriod);
			  }
        const validation = this.validator.validate(this.state);
        this.setState({ validation });
        this.submitted = true;

        if (validation.isValid) {
					// handle actual form submission here
					this.setState({
						successmessage: 'New Absence Created successfully',errormessage: ''
					})
					refreshPage(5000);
					alert('Registered successfully')

        } else {
					this.setState({
						errormessage: 'Cannot Create New Response Make sure all fields are correctly filled', successmessage: ''
					})
					// alert('Cannot Create User Make sure all fields are correctly filled')
        }

  }
  render() {
    const timeOffTypes = this.state.data.timeofftypes;
        let offTypes = timeOffTypes.map( (type, index) => 
            <option value={type} key={index}> {type} </option>

        );
        let validation = this.submitted ?                         // if the form has been submitted at least once
        this.validator.validate(this.state) :   // then check validity every time we render
        this.state.validation     
    return (
      <div>
        <DashBoardNavBar data={this.state.data} />
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-2"></div>
            <div className="col-md-8">
              <div className="form">
              <h5 className="form-header text-center">Create New Absence</h5>

                <form>

                    <div className="form-group ">
                      <label for="type"> Type Of Leave</label>
                      <select id="type" name="type" className="form-control">
                        <option selected>Choose Leave Type</option>
                        {offTypes}
                      </select>


                  </div>
                  <div className="form-row">
                  <div className="form-group col-md-4">
                    <label for="from">From</label>
                    <input type="date" name="from" className="form-control" id="from" placeholder="From" />
                  </div>
                  <div className="form-group col-md-4">
                    <label for="to">To</label>
                    <input type="date" name="to" className="form-control" id="to" placeholder="To" />
                  </div>
                  <div className="form-group col-md-4">
                    <label for="duration">Duration</label>
                    <input type="text" name="duration" className="disabled form-control" id="duration" placeholder="Duration" />
                  </div>
                  </div>
                  <div className="form-group">
                    <label for="reason">Reason For Leave</label>
                    <textarea rows="6" type="date" name="reason" className="form-control" id="reason" placeholder="Reason For Leave" />
                  </div>

                  <button type="submit" className="btn btn-primary">CREATE</button>
                  <button type="submit" className="btn btn-info">CANCEL</button>

                </form>







              </div>
            </div>
            <div className="col-md-2"></div>

          </div>
        </div>
      </div>
    )
  }
}

export default AbsenceForm;
